package com.epam.library.entity;

import jakarta.persistence.*;
@Entity
@Table(name="books")
public class Book {

    //Unique id for each book, autogenerated when creating a book
    @Id
    @GeneratedValue(strategy = GenerationType.IDENTITY)
    private Integer id;

    //The title of the book
    @Column(length = 45, nullable = false, unique = true)
    private String name;

    //The book authorÂ´s name
    @Column(length = 45, nullable = false)
    private String author;

    //The year of publication of the book
    @Column(length = 15, nullable = false)
    private String publicationDate;

    //The number of copies in this library
    @Column(nullable = false)
    private int numOfCopies;

    //The number of books, available for ordering by readers
    @Column(nullable = false)
    private int availableCopies;

    public String getName() {
        return name;
    }

    public void setName(String name) {
        this.name = name;
    }

    public String getAuthor() {
        return author;
    }

    public void setAuthor(String author) {
        this.author = author;
    }

    public String getPublicationDate() {
        return publicationDate;
    }

    public void setPublicationDate(String publicationDate) {
        this.publicationDate = publicationDate;
    }

    public int getNumOfCopies() {
        return numOfCopies;
    }

    public void setNumOfCopies(int numOfCopies) {
        this.numOfCopies = numOfCopies;
    }

    public Integer getId() {
        return id;
    }

    public int getAvailableCopies() {
        return availableCopies;
    }

    public void setAvailableCopies(int availableCopies) {
        this.availableCopies = availableCopies;
    }
}
